--
-- 資料表結構 `research`
--

DROP TABLE IF EXISTS `research`;
CREATE TABLE IF NOT EXISTS `research` (
  `r_id` int(11) NOT NULL AUTO_INCREMENT,
  `r_paper` varchar(250) NOT NULL,
  `r_author` varchar(100) NOT NULL,
  `r_publicdate` date NOT NULL,
  `r_publicwhere` varchar(150) NOT NULL,
  `r_keyword` varchar(150) NOT NULL,
  `r_description` text NOT NULL,
  `r_type` enum('achievement','conference','journal') NOT NULL,
  PRIMARY KEY (`r_id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

--
-- 資料表新增前先清除舊資料 `research`
--

TRUNCATE TABLE `research`;
